        <div >
            <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" style={{ width: '200px', height: '200px', padding: '50px' }} src={image} />
                <Card.Body>
                    <Card.Title>{name}</Card.Title>
                    <Card.Text>
                        Salary: {salary}
