import React from 'react';

const Cart = (props) => {

    function UserRow({ user, index }) {
        const userLink = `/admin-users/${user.id}`;

        const status = user.isActive ? 'Active' : user.isDeleted ? 'Deleted' : 'Inactive';
        const badge = user.isActive ? 'success' : user.isDeleted ? 'danger' : 'warning';

        return (
          <tr>
            <th scope="row">{index + 1}</th>
            <td><Link to={userLink}>{user.firstName} {user.lastName}</Link></td>
            <td>{user.email}</td>
            <td>{user.phone}</td>
            <td>{moment(user.createdAt).format('DD-MM-YYYY hh:mm A')}</td>
            <td><Link to={userLink}><Badge color={badge}>{status}</Badge></Link></td>
          </tr>
        )
      }


    const cart = props.cart;
    //const total = cart.reduce((total, prd) => total + prd.price, 0)
    let total = 0, players = [], salary = [];
    cart.forEach((cart) => {
        total += cart.salary;
        if (!players.includes(cart.name)) {
            players.push(cart.name);
            salary.push(cart.salary);
        }
    })
    return (
        <div>
            <h4>Order Summary</h4>
            <p>Total Player: {cart.length}</p>
            <p>Total Budget: {total}</p>
            <h4>Players Added</h4>
            {players.map((player, index) =>
                      <UserRow key={player} player={player} index={index} />
                    )}
        </div>
    );
};

export default Cart;
